(function($){$.fn.changeWords=function(options){var settings=$.extend({time:1500,animateIn:"zoomIn",animateOut:"zoomOut",selector:"span"},options);var el=this;var ChangeWords=function(){var $this=this;this.wordCount=$(settings.selector,el).size();this.words=$(settings.selector,el);this.firstWord=$(this.words[0]);this.in=function(el){el.addClass("animated "+settings.animateIn).css("display","inline-block")};this.out=function(el){setTimeout(function(){el.addClass("animated "+settings.animateOut).css("display","inline-block")},(settings.time-500))};this.clear=function(el){el.css("display","none").removeClass()};this.__construct=function(){$this.words.filter(function(){return $(this).attr("data-id")!="1"}).css("display","none");$this.out($this.firstWord);var count=1;setInterval(function(){++count;var wordOrder=count,wordSelected,wordDisabled;wordSelected=$this.words.filter(function(){return $(this).attr("data-id")==wordOrder});$this.in(wordSelected);$this.out(wordSelected);wordDisabled=$this.words.filter(function(){return $(this).attr("data-id")!=wordOrder});$this.clear(wordDisabled);if(count==$this.wordCount){count=0}},settings.time)};this.__construct()};new ChangeWords()}}(jQuery));